<?phpif (isset ($_POST['formImageSubmit'])) {    if ((empty ($_POST['title'])) OR (empty ($_POST['description'])) OR (empty ($_POST['filename']))) {        $msg = 'Une des information est manquante.';    } else {//        Les informations sont bien envoyés par Post//        trim sert à enlever les espaces qu'aurait pu mettre l'utilisateur        $title = trim($_POST['title']);        $description = trim($_POST['description']);        $filename = trim($_POST['filename']);        if (isset ($_POST['noentry'])) {            //        Il faut entrer une nouvelle donnée dans le tableau            $image = new Image();            $insertImage = $image->insertImage($title, $description, $filename);            $msg = $insertImage;        } //            il faut mettre à jour une donnée        else {            $image = new Image();            $updateimage = $image->UpdateImageData($title, $description, $filename);            $msg = $updateimage;        }    }}